mutation addToBagRequest($shortSku: String!, $quantity: Int!) {
  addToBagRequest(shortSku: $shortSku, quantity: $quantity) {
    success
    responseData {
      cartUnitsAdded
      orderSubtotal
      orderSubtotalUsd
      freeShippingThreshold {
        freeShippingThreshold
        freeShippingThresholdFmt
        minShippingThreshold
        minShippingThresholdFmt
        __typename
      }
      amountToFreeShipping
      amountToFreeShippingFmt
      pageType
      __typename
    }
    errors {
      message {
        key
        value
        __typename
      }
      status
      __typename
    }
    __typename
  }
}
