query RECOMMENDATIONS_DATA_QUERY($placements: [String]!, $hasUserConsent: Boolean!, $cartItems: [String], $categories: [String], $categoryId: String, $orderId: String, $products: [RecommendationProductDetails], $searchTerm: String) {
  recommendations(
    placements: $placements
    hasUserConsent: $hasUserConsent
    cartItems: $cartItems
    categories: $categories
    categoryId: $categoryId
    orderId: $orderId
    products: $products
    searchTerm: $searchTerm
  ) {
    userId
    errors {
      ...error
      __typename
    }
    mvtDetailed {
      ...mvtDetails
      __typename
    }
    placements {
      id
      title
      name
      products {
        clickTrackingUrl
        response
        __typename
      }
      treatments {
        ...mvtTreatment
        __typename
      }
      __typename
    }
    rcs
    rrAnnotations
    success
    __typename
  }
}

fragment error on Error {
  message {
    key
    value
    __typename
  }
  status
  __typename
}

fragment mvtDetails on MVTDetails {
  control
  eligible
  testId
  treatmentId
  treatmentName
  __typename
}

fragment mvtTreatment on MVTTreatment {
  control
  testId
  testType
  treatmentId
  treatmentName
  __typename
}
