query:
query ($purchaseAmount: Float, $page: String, $hasGiftCard: Boolean) {
  textFor {
    payOverTimeMessagingHeading: pair(pairKey: "payOverTimeMessagingHeading") {
      ...values
      __typename
    }
    payOverTimeMessagingLearnMore: pair(pairKey: "payOverTimeMessagingLearnMore") {
      ...values
      __typename
    }
    glbOr: pair(pairKey: "glbOr") {
      ...values
      __typename
    }
    __typename
  }
  messagePlacement {
    payOverTime(
      purchaseAmount: $purchaseAmount
      page: $page
      hasGiftCard: $hasGiftCard
    ) {
      enabled
      qualified
      klarna {
        enabled
        badgeURL
        badgeAlt
        learnMoreURL
        impressionURL
        __typename
      }
      payPal {
        enabled
        badgeURL
        badgeAlt
        clientSDK
        offer
        __typename
      }
      installmentMessage
      purchaseAmount
      page
      __typename
    }
    __typename
  }
}

fragment values on TextPair {
  key
  value
  __typename
}
